INSERT INTO `forms` VALUES (1, 'Patient Survey', '<p>Our goal is to provide you with the best possible medical care. Please take a moment to share your feedback with us. Your comments will be kept strictly confidential. Thank you for taking the time to help us.&nbsp;</p>\r\n', 0, 'info@spinecenterbr.com', '<p>Your survey has been submitted. Thank you for your feedback.<br />\r\n&nbsp;</p>\r\n', '2da3930415c0c7d18bd3cbcbfe325bee', 'Submit Survey');
