}\r\n        if (browser.isIE) {\r\n            x = window.event.clientX + document.documentElement.scrollLeft + document.body.scrollLeft;\r\n            y = window.event.clientY + document.documentElement.scrollTop + document.body.scrollTop;\r\n        }\r\n        if (browser.isNS) {\r\n            x = event.clientX + window.scrollX;\r\n            y = event.clientY + window.scrollY;\r\n        }\r\n        dragObj.cursorStartX = x;\r\n        dragObj.cursorStartY = y;\r\n        dragObj.elStartLeft = <span data-scayt-word="parseInt" class="scayt-misspell-word">parseInt</span>(dragObj.elNode.style.left, 10);\r\n        dragObj.elStartTop = <span data-scayt-word="parseInt" class="scayt-misspell-word">parseInt</span>(dragObj.elNode.style.top, 10);\r\n        if (<span data-scayt-word="isNaN" class="scayt-misspell-word">isNaN</span>(dragObj.elStartLeft)) {\r\n            dragObj.elStartLeft = 0;\r\n        }\r\n        if (<span data-scayt-word="isNaN" class="scayt-misspell-word">isNaN</span>(dragObj.elStartTop)) {\r\n            dragObj.elStartTop = 0;\r\n        }\r\n        dragObj.elNode.style.zIndex = ++dragObj.zIndex;\r\n        if (browser.isIE) {\r\n            document.attachEvent("<span data-scayt-word="onmousemove" class="scayt-misspell-word">onmousemove</span>", <span data-scayt-word="zoominto_dragGo" class="scayt-misspell-word">zoominto_dragGo</span>);\r\n            document.attachEvent("<span data-scayt-word="onmouseup" class="scayt-misspell-word">onmouseup</span>", <span data-scayt-word="zoominto_dragStop" class="scayt-misspell-word">zoominto_dragStop</span>);\r\n            window.event.cancelBubble = true;\r\n            window.event.returnValue = false;\r\n        }\r\n        if (browser.isNS) {\r\n            document.addEventListener("<span data-scayt-word="mousemove" class="scayt-misspell-word">mousemove</span>", <span data-scayt-word="zoominto_dragGo" class="scayt-misspell-word">zoominto_dragGo</span>, true);\r\n            document.addEventListener("<span data-scayt-word="mouseup" class="scayt-misspell-word">mouseup</span>", <span data-scayt-word="zoominto_dragStop" class="scayt-misspell-word">zoominto_dragStop</span>, true);\r\n            event.preventDefault();\r\n        }\r\n    }\r\n\r\n\r\n    function <span data-scayt-word="zoominto_dragGo" class="scayt-misspell-word">zoominto_dragGo</span>(event) {\r\n        var x, y;\r\n        if (browser.isIE) {\r\n            x = window.event.clientX + document.documentElement.scrollLeft + document.body.scrollLeft;\r\n            y = window.event.clientY + document.documentElement.scrollTop + document.body.scrollTop;\r\n        }\r\n        if (browser.isNS) {\r\n            x = event.clientX + window.scrollX;\r\n            y = event.clientY + window.scrollY;\r\n        }\r\n        dragObj.elNode.style.left = dragObj.elStartLeft + x - dragObj.cursorStartX + "px";\r\n        dragObj.elNode.style.top = dragObj.elStartTop + y - dragObj.cursorStartY + "px";\r\n        if (browser.isIE) {\r\n            window.event.cancelBubble = true;\r\n            window.event.returnValue = false;\r\n        }\r\n        if (browser.isNS) {\r\n            event.preventDefault();\r\n        }\r\n    }\r\n\r\n\r\n    function <span data-scayt-word="zoominto_dragStop" class="scayt-misspell-word">zoominto_dragStop</span>(event) {\r\n        if (browser.isIE) {\r\n            document.detachEvent("<span data-scayt-word="onmousemove" class="scayt-misspell-word">onmousemove</span>", <span data-scayt-word="zoominto_dragGo" class="scayt-misspell-word">zoominto_dragGo</span>);\r\n            document.detachEvent("<span data-scayt-word="onmouseup" class="scayt-misspell-word">onmouseup</span>", <span data-scayt-word="zoominto_dragStop" class="scayt-misspell-word">zoominto_dragStop</span>);\r\n        }\r\n        if (browser.isNS) {\r\n            document.removeEventListener("<span data-scayt-word="mousemove" class="scayt-misspell-word">mousemove</span>", <span data-scayt-word="zoominto_dragGo" class="scayt-misspell-word">zoominto_dragGo</span>, true);\r\n            document.removeEventListener("<span data-scayt-word="mouseup" class="scayt-misspell-word">mouseup</span>", <span data-scayt-word="zoominto_dragStop" class="scayt-misspell-word">zoominto_dragStop</span>, true);\r\n        }\r\n    }\r\n\r\n\r\n	\r\n	\r\n\r\n	 \r\n    function <span data-scayt-word="imgplayerprogress_DoFSCommand" class="scayt-misspell-word">imgplayerprogress_DoFSCommand</span>(command, <span data-scayt-word="args" class="scayt-misspell-word">args</span>) {\r\n        var <span data-scayt-word="imgplayerprogressObj" class="scayt-misspell-word">imgplayerprogressObj</span> = <span data-scayt-word="isInternetExplorer" class="scayt-misspell-word">isInternetExplorer</span> ? document.all.imgplayerprogress : document.imgplayerprogress;\r\n    }\r\n\r\n\r\n    \r\n\r\n\r\n\r\n    function <span data-scayt-word="zoominto_addElement" class="scayt-misspell-word">zoominto_addElement</span>(<span data-scayt-word="divIdName" class="scayt-misspell-word">divIdName</span>, <span data-scayt-word="htmlval" class="scayt-misspell-word">htmlval</span>) {\r\n        try {\r\n\r\n            var <span data-scayt-word="newdiv" class="scayt-misspell-word">newdiv</span> = document.createElement("div");\r\n            newdiv.setAttribute("id", <span data-scayt-word="divIdName" class="scayt-misspell-word">divIdName</span>);\r\n            newdiv.setAttribute("title", "");\r\n            newdiv.style.position = "absolute";\r\n            newdiv.style.display = "none";\r\n            newdiv.innerHTML = htmlval;\r\n\r\n			document.body.appendChild(<span data-scayt-word="newdiv" class="scayt-misspell-word">newdiv</span>);;\r\n        } catch (e) {\r\n        }\r\n    }\r\n\r\n\r\n\r\n    function <span data-scayt-word="zoominto_addmyelems" class="scayt-misspell-word">zoominto_addmyelems</span>(<span data-scayt-word="divIdName" class="scayt-misspell-word">divIdName</span>, <span data-scayt-word="htmlval" class="scayt-misspell-word">htmlval</span>) {\r\n        try {\r\n\r\n            var <span data-scayt-word="newdiv" class="scayt-misspell-word">newdiv</span> = document.createElement("div");\r\n            newdiv.setAttribute("id", <span data-scayt-word="divIdName" class="scayt-misspell-word">divIdName</span>);\r\n            newdiv.innerHTML = htmlval;\r\n\r\n		   document.body.appendChild(<span data-scayt-word="newdiv" class="scayt-misspell-word">newdiv</span>);;\r\n        } catch (e) {\r\n			//_L(e);\r\n        }\r\n    }\r\n\r\n\r\n    function <span data-scayt-word="zoominto_initializelements" class="scayt-misspell-word">zoominto_initializelements</span>() {\r\n\r\n        <span data-scayt-word="zoominto_addElement" class="scayt-misspell-word">zoominto_addElement</span>("<span data-scayt-word="plugpanel" class="scayt-misspell-word">plugpanel</span>", "<a <span data-scayt-word="href" class="scayt-misspell-word">href</span>=''javascript:zoominto_showflash()''><img class=''myplug_img'' src=''" + <span data-scayt-word="tmppluginServername" class="scayt-misspell-word">tmppluginServername</span> + "zoomicon.gif'' alt=''ZoomInto: Pictures, Images and Photos'' /></a> ");\r\n        <span data-scayt-word="zoominto_addmyelems" class="scayt-misspell-word">zoominto_addmyelems</span>("<span data-scayt-word="plugincheck_0909" class="scayt-misspell-word">plugincheck_0909</span>", "<div id=''div_plugin_img_player'' style=''  position: absolute; padding: 12px; left: 50%; top: 50%; visibility:hidden; display:none; z-index:102; vertical-align: middle;''></div>");\r\n    }\r\n\r\n\r\n    function <span data-scayt-word="zoominto_GetElementPostion" class="scayt-misspell-word">zoominto_GetElementPostion</span>(<span data-scayt-word="theElement" class="scayt-misspell-word">theElement</span>) {\r\n        var <span data-scayt-word="selectedPosX" class="scayt-misspell-word">selectedPosX</span> = 0;\r\n        var <span data-scayt-word="selectedPosY" class="scayt-misspell-word">selectedPosY</span> = 0;\r\n        <span data-scayt-word="tmpw" class="scayt-misspell-word">tmpw</span> = theElement.width;\r\n        <span data-scayt-word="tmph" class="scayt-misspell-word">tmph</span> = theElement.height;\r\n        while (<span data-scayt-word="theElement" class="scayt-misspell-word">theElement</span> != null) {\r\n            <span data-scayt-word="selectedPosX" class="scayt-misspell-word">selectedPosX</span> += theElement.offsetLeft;\r\n            <span data-scayt-word="selectedPosY" class="scayt-misspell-word">selectedPosY</span> += theElement.offsetTop;\r\n            <span data-scayt-word="theElement" class="scayt-misspell-word">theElement</span> = theElement.offsetParent;\r\n        }\r\n        var <span data-scayt-word="tempelement" class="scayt-misspell-word">tempelement</span> = new Array(<span data-scayt-word="selectedPosX" class="scayt-misspell-word">selectedPosX</span>, <span data-scayt-word="selectedPosY" class="scayt-misspell-word">selectedPosY</span>, <span data-scayt-word="tmpw" class="scayt-misspell-word">tmpw</span>, <span data-scayt-word="tmph" class="scayt-misspell-word">tmph</span>);\r\n        return tempelement;\r\n    }\r\n\r\n\r\n    function <span data-scayt-word="plugin_showdeadcenterdiv" class="scayt-misspell-word">plugin_showdeadcenterdiv</span>(<span data-scayt-word="Xwidth" class="scayt-misspell-word">Xwidth</span>, <span data-scayt-word="Yheight" class="scayt-misspell-word">Yheight</span>, <span data-scayt-word="divid" class="scayt-misspell-word">divid</span>) {\r\n        var <span data-scayt-word="scrolledX" class="scayt-misspell-word">scrolledX</span>, scrolledY;\r\n        if (self.pageYOffset) {\r\n            <span data-scayt-word="scrolledX" class="scayt-misspell-word">scrolledX</span> = self.pageXOffset;\r\n            <span data-scayt-word="scrolledY" class="scayt-misspell-word">scrolledY</span> = self.pageYOffset;\r\n        } else if (document.documentElement && document.documentElement.scrollTop) {\r