INSERT INTO `specialSections` VALUES (17, 'Health Plans button (Portal)', '<h3>Accepted Health Plans</h3>\r\n\r\n<p class="subtitle">&nbsp;</p>\r\n\r\n<p><img alt="" src="http://spinecenter.kmastage.com/images/uploads/health-plans.png" style="margin: 0px 15px 5px 0px; width: 61px; height: 60px; float: left;" /></p>\r\n\r\n<p style="margin-top: 15px;">We accept most major health plans available in our area.</p>', 'htmltext');
