INSERT INTO `videos` VALUES (3, 'XLIF 3D Animation', 'omkb_S4zTWo', 'The XLIFÂ® (eXtreme Lateral Interbody Fusion) procedure provides relief to patients who cannot tolerate a larger, open back surgery because of the increased risks of longer anesthesia time, blood loss, hospitalization, and recovery. ', 'spine', 30, 0, 1);
INSERT INTO `videos` VALUES (4, 'Axial LIF Procedure', 'mycysi1WM7k', 'The Axial LIF procedure is a newly developed minimally invasive fusion technique that allows fusion of the lowest or the lower two levels of the low back through a tiny incision. This incision is placed at the base of the buttock far below the waist line. This is cosmetically advantageous and also allows us to avoid any incision on the sensitive muscles of the lower back.', '3:33', 40, 0, 1);
INSERT INTO `videos` VALUES (5, 'Insert Endoscope', '_blsBG7NjDc', 'A ten second video of an an endoscopic insertion.', '0:10', 50, 0, 1);
INSERT INTO `videos` VALUES (6, 'Endoscopic Removal of Disc Herniation ', 'twYKpybVU0c', 'Endoscopic removal of disc herniation ', '0:06', 60, 0, 1);
